mapper.select:
SELECT user_id, reg_date, user_name, email, address
FROM   tb_user
order by reg_date desc, user_name;

SELECT user_id, reg_date, user_name, email, address
FROM   tb_user
WHERE  user_id = #{userId}

SELECT user_id, reg_date, user_name, email, address
FROM   tb_user
WHERE  user_id = #{userId}
AND    user_pwd = #{userPwd}

SELECT count(*) as cnt
FROM   tb_user
WHERE  user_id = #{userId}

mapper.update:
UPDATE TB_USER SET
   user_pwd = #{userPwd},
   upd_id   = #{userId},
   upd_dt   = now()
WHERE user_id = #{userId}
;
